// Normaliza entrada y mapea a public.recursos
const input = $input.all();
if (input.length === 0) return [];

const src = input[0]?.json ?? {};
const registros = Array.isArray(src.value) ? src.value : (Array.isArray(src) ? src : [src]);
const items = [];

// helpers
const cleanDate = (v) => {
  const s = String(v ?? '').trim();
  if (!s || s === '0001-01-01') return '';
  return s;
};
const toBool = (v) => {
  const s = String(v).toLowerCase();
  return s === 'true' || s === '1' || s === 't' || s === 'yes';
};

// Obtener companyName del nodo Build sync_state map
const companyName = $items('Build sync_state map')[0]?.json?.company || '';

for (const r of registros) {
  const no = String(r.no ?? r.code ?? '').trim();
  if (!no) continue;

  const name = String(r.name ?? '').trim();
  const email = String(r.email ?? r.arbvrneMail ?? '').trim();
  const globalDimension1Code = String(
    r.globalDimension1Code ?? r.global_dimension1_code ?? r.department ?? ''
  ).trim();
  const calendario = String(
    r.calendario ?? r.Calendario ?? r.calendar_type ?? r.calendarType ?? r.calendar ?? ''
  ).trim();
  const fechaDeBaja = cleanDate(r.fechaDeBaja ?? r.fecha_de_baja);
  const fechaDeAlta = cleanDate(r.fechaDeAlta ?? r.fecha_de_alta);
  const subcontratacion = (typeof r.subcontratacion === 'boolean') ? r.subcontratacion : toBool(r.subcontratacion);
  const perfil = String(r.perfil ?? '').trim();
  const lastModifiedDateTime = String(r.lastModifiedDateTime ?? r.last_modified_datetime ?? '').trim();

  items.push({
    json: {
      companyName,
      no,
      name,
      arbvrneMail: email,
      globalDimension1Code,
      calendario,
      fechaDeBaja,
      fechaDeAlta,
      subcontratacion,
      perfil,
      lastModifiedDateTime,
    },
  });
}

return items;

// Normaliza entrada y mapea a public.ps_year
const input = $input.all();
if (input.length === 0) return [];

const src = input[0]?.json ?? {};
const registros = Array.isArray(src.value) ? src.value : (Array.isArray(src) ? src : [src]);
const items = [];

// Obtener companyName del nodo Build sync_state map
const companyName = $items('Build sync_state map')[0]?.json?.company || '';

for (const r of registros) {
  const psYear = String(r.year ?? r.Year ?? r.PS_Year ?? r.ps_year ?? '').trim();
  if (!psYear || psYear === '0') continue;

  items.push({
    json: {
      companyName,
      PS_Year: parseInt(psYear) || 0,
    },
  });
}

return items;

const input = $input.all();
if (input.length === 0) return [];

const companyName = $('Build sync_state map').first().json.company;
const data = input[0].json;
const proyectos = data.value || [];
const items = [];

const allowed = new Set(['Planning','Quote','Open','Completed','Lost']);

// helpers
const cleanDate = (v) => {
  const s = String(v ?? '').trim();
  if (!s || s === '0001-01-01') return '';
  return s;
};
const toBool = (v) => {
  const s = String(v).toLowerCase();
  return s === 'true' || s === '1' || s === 't' || s === 'yes';
};

for (const p of proyectos) {
  // DEBUG: Log para ver los datos originales
  console.log('Proyecto original:', JSON.stringify(p));

  const statusRaw = p.estado ?? p.Estado ?? p.status ?? '';
  const status = String(statusRaw).trim();

  // Normalizar status: si no está en la lista permitida, usar 'Open'
  const normalizedStatus = allowed.has(status) ? status : 'Open';

  // Manejar valores no numéricos en Probability - MÁS ROBUSTO
  let probability = 0;
  const probValue = p.Probability ?? p.probability;

  if (probValue !== undefined && probValue !== null) {
    if (typeof probValue === 'string' && probValue.trim() === '') {
      probability = 0;
    } else {
      const num = Number(probValue);
      if (!isNaN(num) && isFinite(num)) {
        probability = num;
      }
    }
  }

  // DEBUG: Log para ver la transformación
  console.log('Probability transformado:', probability, 'de:', probValue);

  const no = String(p.no || '').trim();
  const description = String(p.description || '').trim();
  const departamento = String(p.departamento || p.globalDimension1Code || '').trim();
  const tipoProyecto = String(p.TipoProyecto ?? p.tipoProyecto ?? '').trim();
  const fechaFin = cleanDate(p.Fechafin ?? p.fechaFin ?? p.endingDate ?? '');
  const doNotConsolidate = (typeof p.DoNotConsolidate === 'boolean') ? p.DoNotConsolidate : toBool(p.DoNotConsolidate);
  const responsible = String(p.Responsible ?? p.responsible ?? '').trim();
  const lastModifiedDateTime = String(p.lastModifiedDateTime ?? p.last_modified_datetime ?? '').trim();

  // Crear objeto con nombres de campo en minúsculas para evitar problemas
  const proyectoTransformado = {
    companyName,
    no,
    description,
    departamento,
    probability: probability,  // minúscula
    tipoProyecto: tipoProyecto,  // minúscula
    estado: normalizedStatus,  // minúscula
    fechaFin: fechaFin,  // minúscula
    doNotConsolidate: doNotConsolidate,
    responsible: responsible,  // minúscula
    lastModifiedDateTime,
  };

  // DEBUG: Verificar el objeto transformado
  console.log('Proyecto transformado:', JSON.stringify(proyectoTransformado));

  items.push({
    json: proyectoTransformado,
  });
}

return items;

// Normaliza entrada y mapea a public.equipo_proyectos
const input = $input.all();
if (input.length === 0) return [];

const src = input[0]?.json ?? {};
const registros = Array.isArray(src.value) ? src.value : (Array.isArray(src) ? src : [src]);
const items = [];

// Obtener companyName del nodo Build sync_state map
const companyName = $items('Build sync_state map')[0]?.json?.company || '';

for (const r of registros) {
  const noProyecto = String(r.NoProyecto ?? r.noProyecto ?? r.projectNo ?? '').trim();
  const noRecurso = String(r.NoRecurso ?? r.noRecurso ?? r.resourceNo ?? '').trim();

  if (!noProyecto || !noRecurso) continue;

  const lastModifiedDateTime = String(r.lastModifiedDateTime ?? r.last_modified_datetime ?? '').trim();

  items.push({
    json: {
      companyName,
      noProyecto,
      noRecurso,
      lastModifiedDateTime,
    },
  });
}

return items;

const p = $json.params || {};
const slugRaw = (p.company || $json.query?.company || '').toString().trim().toLowerCase();
const slug = encodeURI(slugRaw);
const map = {
  'psi': { companyName: 'Power Solution Iberia SL', companyId: 'ca9dc1bf-54ee-ed11-884a-000d3a455d5b' },
  'pslab': { companyName: 'PS LAB CONSULTING SL', companyId: '656f8f0e-2bf4-ed11-8848-000d3a4baf18' }
};
const chosen = map[slug];
if (!chosen) {
  return [{ json: { error: 'Invalid company', slug } }];
}
return [{ json: { companyName: chosen.companyName, companyId: chosen.companyId, slug } }];

const rows = $input.all().flatMap(i => Array.isArray(i.json) ? i.json : [i.json]);

// Entidades que nos interesan
const entities = ['recursos','ps_year','proyectos','equipo_proyectos'];

const byEntity = {};
for (const r of rows) {
  const e = String(r.entity || '').trim();
  const raw = String(r.last_sync_at || '').trim();
  if (!e || !raw) continue;
  const iso = new Date(raw.replace(' ', 'T')).toISOString();
  byEntity[e] = iso;
}

// Para entidades faltantes, usar una fecha muy antigua para obtener todos los registros
const defaultDate = '1900-01-01T00:00:00.000Z';
for (const entity of entities) {
  if (!byEntity[entity]) {
    byEntity[entity] = defaultDate;
  }
}

// Devolver un elemento por cada entidad para que cada nodo de API reciba datos
const items = [];
const companyName = $('Resolve Company').first().json.companyName;
const companyId = $('Resolve Company').first().json.companyId;

for (const entity of entities) {
  items.push({
    json: {
      syncStateByEntity: byEntity,
      company: companyName,
      companyId: companyId,
      entity: entity
    }
  });
}

return items;
