// Procesar datos de la solicitud de aprobaci贸n
const input = $input.all()[0];

// Debug: mostrar estructura completa del input
console.log('Input structure:', Object.keys(input));
console.log('Input.body:', input.body);
console.log('Input.json:', input.json);

// Intentar obtener los datos de diferentes formas
let payload = null;

if (input.body && typeof input.body === 'object') {
  payload = input.body;
  console.log('Using input.body');
} else if (input.json && typeof input.json === 'object') {
  payload = input.json;
  console.log('Using input.json');
} else if (input.data && typeof input.data === 'object') {
  payload = input.data;
  console.log('Using input.data');
} else {
  // Si no hay datos en los campos esperados, usar todo el input
  payload = input;
  console.log('Using full input');
}

console.log('Payload final:', JSON.stringify(payload, null, 2));

// Validar que tenemos un payload
if (!payload || typeof payload !== 'object') {
  return [{
    json: {
      error: 'No se pudieron obtener los datos del webhook',
      status: 'error',
      debug: {
        inputKeys: Object.keys(input),
        body: input.body,
        json: input.json,
        data: input.data
      }
    }
  }];
}

// Extraer datos del payload con validaci贸n
const header_id = payload.header_id;
const requester_email = payload.requester_email || 'desconocido';
const approver_codes = payload.approver_codes || [];
const recipients = payload.recipients || [];
const env = payload.env || 'desconocido';

console.log('Datos extra铆dos:');
console.log('header_id:', header_id);
console.log('requester_email:', requester_email);
console.log('approver_codes:', approver_codes);
console.log('recipients:', recipients);
console.log('env:', env);

// Validar datos requeridos
if (!header_id) {
  return [{
    json: {
      error: 'header_id es requerido',
      status: 'error',
      debug: {
        payload: payload,
        extractedHeaderId: header_id
      }
    }
  }];
}

// Construir objeto de datos para el email
const emailData = {
  header_id,
  requester_email,
  approver_codes,
  recipients,
  env,
  timestamp: new Date().toISOString(),
  subject: `Solicitud de Aprobaci贸n - Timesheet #${header_id.substring(0, 8)}`,
  message: `Se ha enviado una solicitud de aprobaci贸n para el timesheet ${header_id}.

Detalles:
- Solicitante: ${requester_email}
- Aprobadores: ${approver_codes.length} responsables
- Destinatarios: ${recipients.length} emails
- Entorno: ${env}

Por favor, revisa la solicitud en el sistema de timesheet.`
};

console.log('EmailData construido:', JSON.stringify(emailData, null, 2));

return [{
  json: emailData
}];

// Preparar email para cada destinatario
// Obtener datos originales del nodo anterior
const emailData = $('Procesar Datos Aprobaci贸n').first().json;
const currentRecipient = $('Split por Destinatario').item.json;

// Validar que tenemos los datos necesarios
if (!emailData || !emailData.header_id) {
  return [{
    json: {
      error: 'Datos de email no disponibles',
      status: 'error'
    }
  }];
}

// Formato correcto para Microsoft Outlook
const emailConfig = {
  toRecipients: currentRecipient,
  subject: emailData.subject,
  html: `
    <!DOCTYPE html>
    <html>
    <head>
      <meta charset="utf-8">
      <style>
        body { font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; line-height: 1.6; color: #333; }
        .container { max-width: 600px; margin: 0 auto; padding: 20px; }
        .header { background: #007c89; color: white; padding: 20px; text-align: center; }
        .content { padding: 20px; background: #f9f9f9; }
        .footer { padding: 20px; text-align: center; color: #666; font-size: 12px; }
        .button { background: #007c89; color: white; padding: 10px 20px; text-decoration: none; border-radius: 4px; display: inline-block; }
      </style>
    </head>
    <body>
      <div class="container">
        <div class="header">
          <h1> Solicitud de Aprobaci贸n</h1>
        </div>
        <div class="content">
          <h2>Timesheet Pendiente de Aprobaci贸n</h2>
          <p><strong>ID:</strong> ${emailData.header_id}</p>
          <p><strong>Solicitante:</strong> ${emailData.requester_email}</p>
          <p><strong>Fecha:</strong> ${new Date(emailData.timestamp).toLocaleString('es-ES')}</p>

          <p>Se ha enviado una solicitud de aprobaci贸n que requiere tu revisi贸n.</p>

          <div style="text-align: center; margin: 30px 0;">
            <a href="${emailData.env === 'testing' ? 'https://testingapp.powersolution.es' : 'https://app.powersolution.es'}/my-timesheet-app/aprobacion" class="button">
              Revisar Solicitud
            </a>
          </div>

          <p><em>Este es un mensaje autom谩tico, por favor no respondas a este email.</em></p>
        </div>
        <div class="footer">
          <p>Power Solution - Sistema de Timesheet</p>
          <p>Entorno: ${emailData.env || 'desconocido'}</p>
        </div>
      </div>
    </body>
    </html>
  `
};

return [{
  json: {
    ...emailData,
    emailConfig
  }
}];

// Recopilar resultados del env铆o
const emailResults = $input.all();
const emailData = $('Procesar Datos Aprobaci贸n').first().json;

const sentEmails = emailResults
  .filter(result => result.json && result.json.success)
  .map(result => result.json.emailConfig.toRecipients);

const failedEmails = emailResults
  .filter(result => result.json && !result.json.success)
  .map(result => ({
    to: result.json.emailConfig.toRecipients,
    error: result.json.error
  }));

return [{
  json: {
    header_id: emailData.header_id,
    total_recipients: emailData.recipients.length,
    sent_count: sentEmails.length,
    failed_count: failedEmails.length,
    sent_emails: sentEmails,
    failed_emails: failedEmails,
    timestamp: new Date().toISOString(),
    status: failedEmails.length === 0 ? 'success' : 'partial'
  }
}];

