parameters.jsCode:
// Procesar datos de la solicitud de aprobaci贸n con validaci贸n mejorada
const input = $input.all()[0];
const payload = input.json;

const {
  header_id,
  requester_email,
  approver_codes = [],
  recipients = [],
  env
} = payload;

// Validar datos requeridos
if (!header_id) {
  return [{
    json: {
      error: 'header_id es requerido',
      status: 'error'
    }
  }];
}

// Validar formato de emails de destinatarios
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
const validRecipients = recipients.filter(email => emailRegex.test(email));
const invalidRecipients = recipients.filter(email => !emailRegex.test(email));

if (validRecipients.length === 0) {
  return [{
    json: {
      error: 'No hay destinatarios v谩lidos',
      invalid_emails: invalidRecipients,
      status: 'error'
    }
  }];
}

// Construir objeto de datos para el email
const emailData = {
  header_id,
  requester_email: requester_email || 'desconocido',
  approver_codes,
  recipients: validRecipients,
  invalid_recipients: invalidRecipients,
  env,
  timestamp: new Date().toISOString(),
  subject: `Solicitud de Aprobaci贸n - Timesheet #${header_id.substring(0, 8)}`,
  message: `Se ha enviado una solicitud de aprobaci贸n para el timesheet ${header_id}.

Detalles:
- Solicitante: ${requester_email || 'desconocido'}
- Aprobadores: ${approver_codes.length} responsables
- Destinatarios v谩lidos: ${validRecipients.length} emails
- Destinatarios inv谩lidos: ${invalidRecipients.length} emails
- Entorno: ${env || 'desconocido'}

Por favor, revisa la solicitud en el sistema de timesheet.`
};

return [{
  json: emailData
}];

// Preparar email para cada destinatario
const emailData = $input.all()[0].json;
const currentRecipient = $('Split por Destinatario').item.json;

const emailConfig = {
  to: currentRecipient,
  subject: emailData.subject,
  text: emailData.message,
  html: `
    <!DOCTYPE html>
    <html>
    <head>
      <meta charset="utf-8">
      <style>
        body { font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; line-height: 1.6; color: #333; }
        .container { max-width: 600px; margin: 0 auto; padding: 20px; }
        .header { background: #007c89; color: white; padding: 20px; text-align: center; }
        .content { padding: 20px; background: #f9f9f9; }
        .footer { padding: 20px; text-align: center; color: #666; font-size: 12px; }
        .button { background: #007c89; color: white; padding: 10px 20px; text-decoration: none; border-radius: 4px; display: inline-block; }
      </style>
    </head>
    <body>
      <div class="container">
        <div class="header">
          <h1> Solicitud de Aprobaci贸n</h1>
        </div>
        <div class="content">
          <h2>Timesheet Pendiente de Aprobaci贸n</h2>
          <p><strong>ID:</strong> ${emailData.header_id}</p>
          <p><strong>Solicitante:</strong> ${emailData.requester_email}</p>
          <p><strong>Fecha:</strong> ${new Date(emailData.timestamp).toLocaleString('es-ES')}</p>

          <p>Se ha enviado una solicitud de aprobaci贸n que requiere tu revisi贸n.</p>

          <div style="text-align: center; margin: 30px 0;">
            <a href="${emailData.env === 'testing' ? 'https://testingapp.powersolution.es' : 'https://app.powersolution.es'}/my-timesheet-app/aprobacion" class="button">
              Revisar Solicitud
            </a>
          </div>

          <p><em>Este es un mensaje autom谩tico, por favor no respondas a este email.</em></p>
        </div>
        <div class="footer">
          <p>Power Solution - Sistema de Timesheet</p>
          <p>Entorno: ${emailData.env || 'desconocido'}</p>
        </div>
      </div>
    </body>
    </html>
  `
};

return [{
  json: {
    ...emailData,
    emailConfig
  }
}];

// Recopilar resultados del env铆o via Microsoft Graph
const emailResults = $input.all();
const emailData = $('Procesar Datos Aprobaci贸n').first().json;

// Microsoft Graph devuelve un objeto con status 202 si es exitoso
const sentEmails = emailResults
  .filter(result => result.json && result.json.status === 202)
  .map(result => $('Split por Destinatario').item.json);

const failedEmails = emailResults
  .filter(result => result.json && result.json.status !== 202)
  .map(result => ({
    to: $('Split por Destinatario').item.json,
    error: result.json.error || 'Error desconocido'
  }));

return [{
  json: {
    header_id: emailData.header_id,
    total_recipients: emailData.recipients.length,
    sent_count: sentEmails.length,
    failed_count: failedEmails.length,
    sent_emails: sentEmails,
    failed_emails: failedEmails,
    timestamp: new Date().toISOString(),
    status: failedEmails.length === 0 ? 'success' : 'partial'
  }
}];

parameters.responseBody:
={{ {
